        "nodejs": "latest"
      }
    },
    "build": {
      "commands": [
        "npm install",
        "npm run check --if-present",
        "npm run lint --if-present",
        "npm run test --if-present",
        "npm run build",
        "cd infra",
        "npm install",
        "npm run build",
        "npx -y cdk synth --context codeConnectionArn=arn:aws:codeconnections:us-east-1:492267476755:connection/b723259a-c57f-4245-9416-a59676b72429 --context repositoryName=PawRush/swyxkit --context branchName=deploy-to-aws"
      ]
    }
  },
  "artifacts": {
    "base-directory": "infra/cdk.out",
    "files": [
      "**/*"
    ]
  }
}

aws:cdk:cloudformation:props.functionCode:
function handler(event) {
  const request = event.request;
  let uri = request.uri;
  if (!uri.includes('.')) {
    if (!uri.endsWith('/')) uri += '/';
    request.uri = uri + 'index.html';
  }
  return request;
}
